import React, { useContext } from "react";
import "./settings.css";
import Sidebar from "../../components/Sidebar/Sidebar";
import { Context } from "../../context/Context";
import axios from "axios";

export default function settings() {
  const [email, setEmail] = useState(null);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [file, setFile] = useState("");

  const { user, dispatch } = useContext(Context);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const updatedUser = {
      userId: user._id,
      username: username,
      email: email,
      password: password,
    };
    if (file) {
      const data = new FormData();
      const filename = Date.now() + file.name;
      data.append("name", filename);
      data.append("file", file);
      updatedUser.profilePic = filename;
      try {
        await axios.post("/upload", data);
      } catch (err) {
        console.log(err);
      }
    }
    try {
      const res = await axios.post("/posts", newPost);
      window.location.replace("/post/" + res.data._id);
    } catch (err) {
      console.log(err);
    }
  };
  return (
    <div className="settings">
      <div className="settingsWrapper">
        <div className="settingsTitle">
          <span className="settingsUpdateTitle">Update Account</span>
          <span className="settingsDeleteTitle">Delete account</span>
        </div>
        <form action="" className="settingsForm">
          <label htmlFor="">profilePicture</label>
          <div className="settingsProfilePic">
            <img
              // src="https://us.123rf.com/450wm/tomertu/tomertu1711/tomertu171100465/89971836-image-of-open-antique-book-on-wooden-table-with-glitter-overlay.jpg?ver=6"
              src={user.profilePic}
              alt=""
            />
            <label htmlFor="fileInput">
              <i className="settingsProfilePicIcon fa-solid fa-circle-user"></i>
            </label>
            <input type="file" id="fileInput style={{display:none}}" />
          </div>
          <label>Username</label>
          <input type="text" placeholder="name" />
          <label>Email-id</label>
          <input type="email" placeholder="xyz@gmail.com" />
          <label>Password</label>
          <input type="password" />
          <button className="settingsSubmit">Update</button>
        </form>
      </div>
      <Sidebar />
    </div>
  );
}
